import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [jobTitle, setJobTitle] = useState('');
  const [resumeText, setResumeText] = useState('');
  const [missingTechnicalSkills, setMissingTechnicalSkills] = useState([]);
  const [missingSoftSkills, setMissingSoftSkills] = useState([]);
  const [resumeScore, setResumeScore] = useState(0);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/analyze-resume', {
        jobTitle,
        resumeText,
      });
      setMissingTechnicalSkills(response.data.technical_skills);
      setMissingSoftSkills(response.data.soft_skills);
      setResumeScore(response.data.resume_score);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="App">
     <center> <h1>SkillBridge</h1></center>
      <form onSubmit={handleSubmit}>
        <label>
          Select Job Title:
          <select value={jobTitle} onChange={(e) => setJobTitle(e.target.value)}>
            <option value="Full-Stack-Java">Full-Stack-Java</option>
            <option value="Full-Stack-Python">Full-Stack-Python</option>
            <option value="Data-Engineer">Data Engineer</option>
            <option value="Data-Analyst">Data Analyst</option>
            <option value="Tester">Tester</option>
          </select>
        </label>
        <br />
        <label>
          Paste your resume (plain text):
          <textarea
            value={resumeText}
            onChange={(e) => setResumeText(e.target.value)}
          ></textarea>
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
      {/* {missingTechnicalSkills.length > 0 && (
        <div>
          <h2>Missing Technical Skills:</h2>
          <ul>
            {missingTechnicalSkills.map((skill, index) => (
              <li key={index}>{skill}</li>
            ))}
          </ul>
        </div>
      )} */}
      {Object.keys(missingTechnicalSkills).length > 0 && (
  <div>
    <h2>Missing Technical Skills:</h2>
    <ul>
    {Object.keys(missingTechnicalSkills).map((skill, index) => (
        <li key={index}>
          <a href={missingTechnicalSkills[skill]}>{skill}</a>
        </li>
      ))}
    </ul>
  </div>
)}
      {missingSoftSkills.length > 0 && (
        <div>
          <h2>Missing Soft Skills:</h2>
          <ul>
            {missingSoftSkills.map((skill, index) => (
              <li key={index}>{skill}</li>
            ))}
          </ul>
        </div>
      )}
      <div>
        <h2>Resume Score:</h2>
        <p>{resumeScore}</p>
      </div>
    </div>
  );
}

export default App;
