import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { TextField, Button, Box, Typography, CircularProgress } from '@mui/material';

function RoleSelectionPage() {
  const [selectedRole, setSelectedRole] = useState('');
  const [resumeText, setResumeText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleRoleChange = (event) => {
    setSelectedRole(event.target.value);
  };

  const handleScan = () => {
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
      navigate('/resume-ats-score'); // Navigate to the ATS score page
    }, 3000); // Delay for 3 seconds
  };

  const handleCancel = () => {
    setResumeText('');
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh', p: 3 }}>
      <Typography variant="h4" sx={{ mb: 4, color: 'primary.main' }}>Select Your Role</Typography>
      <Box component="form" sx={{ maxWidth: 500, width: '100%', mb: 3 }}>
        <TextField
          select
          label="Role"
          value={selectedRole}
          onChange={handleRoleChange}
          fullWidth
          SelectProps={{
            native: true,
          }}
          variant="outlined"
          sx={{ mb: 3 }}
        >
          <option value="" disabled>Select a role...</option>
          <option value="Business Analyst">Business Analyst</option>
          <option value="Software Developer">Software Developer</option>
          <option value="Full Stack Developer">Full Stack Developer</option>
        </TextField>
        <TextField
          label="Paste your resume"
          value={resumeText}
          onChange={(e) => setResumeText(e.target.value)}
          fullWidth
          multiline
          rows={8}
          variant="outlined"
          sx={{ mb: 3 }}
        />
        <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
          <Button variant="contained" color="primary" onClick={handleScan} disabled={isLoading}>
            {isLoading ? <CircularProgress size={24} color="inherit" /> : 'Scan'}
          </Button>
          <Button variant="outlined" color="secondary" onClick={handleCancel}>
            Cancel
          </Button>
        </Box>
      </Box>
    </Box>
  );
}

export default RoleSelectionPage;
